import React from 'react';
import * as d3 from 'd3';
// import { ThemeProvider } from 'styled-components';

const random = (min: number, max: number) =>
  Math.floor(Math.random() * (max - min)) + min;

const generate = (length: number) =>
  Array.from({ length }, (_) => ({ height: random(10, 200), width: 20 }));

const App = () => {
  const data = React.useMemo(() => generate(10), []);

  const square = d3
    .area()
    .x(() => 0)
    .x1((p) => 10)
    .y1((p) => 10);

  return (
    <svg>
      <path d={square(new Array(10)) ?? undefined}></path>
    </svg>
  );
};

export default App;
