// import { GlobalStyle, theme } from '@d3-tests/design-system';
import React, { KeyboardEvent } from 'react';
import * as d3Shape from 'd3-shape';
import * as d3 from 'd3';
import styled from 'styled-components';
// import { ThemeProvider } from 'styled-components';

interface Point {
  height: number;
  width: number;
  index: number;
}

interface HLine {
  y: number;
}

interface VLine {
  x: number;
}

const MAX_SIZE = 800;
const DETALIZATION = 30;

const random = (min: number, max: number) =>
  Math.floor(Math.random() * (max - min)) + min;

const App = () => {
  const [detalization, setDetalization] = React.useState(DETALIZATION);

  const heights = React.useMemo(
    () => Array.from({ length: 1000 }, (_, i) => random(10, MAX_SIZE)),
    [],
  );

  const points = React.useMemo<Point[]>(
    () =>
      Array.from({ length: Math.ceil(detalization + 1) }, (_, i) => ({
        height: heights[i],
        width: MAX_SIZE / detalization,
        index: i,
      })),
    [detalization, heights],
  );

  const hLines = React.useMemo(() => {
    const step = MAX_SIZE / detalization;

    return Array.from<undefined, HLine>(
      { length: detalization + 1 },
      (_, i) => ({
        y: step * i,
      }),
    );
  }, [detalization]);

  const vLines = React.useMemo(() => {
    const step = MAX_SIZE / detalization;

    return Array.from<undefined, VLine>(
      { length: detalization + 1 },
      (_, i) => ({
        x: step * i,
      }),
    );
  }, [detalization]);

  const square = d3Shape
    .area<Point>()
    .x((p) => p.index * p.width)
    .y0((p) => 0)
    .y1((p) => p.height)
    .curve(d3Shape.curveStepAfter);

  React.useEffect(() => {
    const maxValue = MAX_SIZE / 2;
    const minValue = 3;

    const handler = (e: WheelEvent) => {
      setDetalization((prev) => {
        const coeficient = prev / 20;

        const nextValue = prev + Math.sign(e.deltaY) * coeficient;

        return Math.min(Math.max(minValue, nextValue), maxValue);
      });
    };

    window.addEventListener('wheel', handler);

    return () => {
      window.removeEventListener('wheel', handler);
    };
  }, []);

  React.useEffect(() => {
    const handler = (e: globalThis.KeyboardEvent) => {
      if (e.code !== 'KeyR') return;
    };

    document.addEventListener('keydown', handler);

    return () => {
      document.removeEventListener('keydown', handler);
    };
  }, []);

  return (
    <App.Container>
      <App.Canvas>
        {hLines.map((l) => (
          <line
            y1={l.y}
            y2={l.y}
            x1={0}
            x2={MAX_SIZE}
            strokeWidth={1}
            stroke="#fff"
            key={l.y}
          />
        ))}

        {vLines.map((l) => (
          <line
            y1={0}
            y2={MAX_SIZE}
            x1={l.x}
            x2={l.x}
            strokeWidth={1}
            stroke="#fff"
            key={l.x}
          />
        ))}

        <path d={square(points) ?? undefined} fill="#00aaff"></path>

        {points.map((p) => (
          <>
            {p.width > 30 && (
              <text
                key={p.index}
                fill="black"
                x={p.index * p.width + 2}
                y={p.height - 10}
              >
                {p.height}
              </text>
            )}
          </>
        ))}
      </App.Canvas>
    </App.Container>
  );
};

App.Container = styled.div`
  width: 100%;
  height: 100%;

  display: flex;
  justify-content: center;
  align-items: center;
`;

App.Canvas = styled.svg`
  width: ${MAX_SIZE}px;
  height: ${MAX_SIZE}px;
`;

export default App;
