// import { GlobalStyle, theme } from '@d3-tests/design-system';
import React from 'react';
import * as d3 from 'd3-shape';
// import { ThemeProvider } from 'styled-components';

interface Point {
  height: number;
  width: number;
  index: number;
}

const MAX_HEIGHT = 300;

const random = (min: number, max: number) =>
  Math.floor(Math.random() * (max - min)) + min;

const generate = (length: number): Point[] =>
  Array.from({ length }, (_, i) => ({
    height: random(10, MAX_HEIGHT),
    width: 20,
    index: i,
  }));

const App = () => {
  const data = React.useMemo(() => generate(50), []);

  const square = d3
    .area<Point>()
    .x((p) => p.index * p.width)
    .y0((p) => 0)
    .y1((p) => p.height);

  return (
    <svg height={MAX_HEIGHT}>
      <path d={square(data) ?? undefined}></path>
    </svg>
  );
};

export default App;
