// import { GlobalStyle, theme } from '@d3-tests/design-system';
import React from 'react';
import * as d3 from 'd3-shape';
// import { ThemeProvider } from 'styled-components';

interface Point {
  height: number;
  width: number;
  index: number;
}

interface VLine {
  y: number;
}

const MAX_HEIGHT = 500;

const random = (min: number, max: number) =>
  Math.floor(Math.random() * (max - min)) + min;

const App = () => {
  const points = React.useMemo<Point[]>(
    () =>
      Array.from({ length: 50 }, (_, i) => ({
        height: random(10, MAX_HEIGHT),
        width: 20,
        index: i,
      })),
    [],
  );

  const lines = React.useMemo(() => {
    const length = 10;
    const step = MAX_HEIGHT / length;

    return Array.from<undefined, VLine>({ length }, (_, i) => ({
      y: step * i,
    }));
  }, []);

  const square = d3
    .area<Point>()
    .x((p) => p.index * p.width)
    .y0((p) => 0)
    .y1((p) => p.height);

  return (
    <svg height={MAX_HEIGHT} width={MAX_HEIGHT}>
      <path d={square(points) ?? undefined}></path>

      {lines.map((l) => (
        <line
          y1={l.y}
          y2={l.y}
          width="100%"
          strokeWidth="2px"
          stroke="#000000"
        />
      ))}
    </svg>
  );
};

export default App;
