// import { GlobalStyle, theme } from '@d3-tests/design-system';
import React from 'react';
import * as d3 from 'd3';
// import { ThemeProvider } from 'styled-components';

const random = (min: number, max: number) =>
  Math.floor(Math.random() * (max - min)) + min;

const generate = (length: number) =>
  Array.from({ length }, (_, i) => ({
    height: random(10, 200),
    width: 20,
    index: i,
  }));

const App = () => {
  const data = React.useMemo(() => generate(10), []);

  const square = d3
    .area()
    //@ts-ignore
    .x((p) => p.i * p.width)
    .y0((p) => 0)
    //@ts-ignore
    .y1((p) => p.height);

  return (
    <svg>
      {
        //@ts-ignore
        <path d={square(data) ?? undefined}></path>
      }
    </svg>
  );
};

export default App;
