// import { GlobalStyle, theme } from '@d3-tests/design-system';
import React from 'react';
import * as d3 from 'd3-shape';
// import { ThemeProvider } from 'styled-components';

interface Point {
  height: number;
  width: number;
  index: number;
}

interface HLine {
  y: number;
}

interface VLine {
  x: number;
}

const MAX_SIZE = 500;
const DETALIZATION = 10;

const random = (min: number, max: number) =>
  Math.floor(Math.random() * (max - min)) + min;

const App = () => {
  const points = React.useMemo<Point[]>(
    () =>
      Array.from({ length: 50 }, (_, i) => ({
        height: random(10, MAX_SIZE),
        width: 20,
        index: i,
      })),
    [],
  );

  const hLines = React.useMemo(() => {
    const step = MAX_SIZE / DETALIZATION;

    return Array.from<undefined, HLine>({ length: DETALIZATION }, (_, i) => ({
      y: step * i,
    }));
  }, []);

  const vLines = React.useMemo(() => {
    const step = MAX_SIZE / DETALIZATION;

    return Array.from<undefined, VLine>({ length: DETALIZATION }, (_, i) => ({
      x: step * i,
    }));
  }, []);

  const square = d3
    .area<Point>()
    .x((p) => p.index * p.width)
    .y0((p) => 0)
    .y1((p) => p.height);

  return (
    <svg height={MAX_SIZE} width={MAX_SIZE}>
      <path d={square(points) ?? undefined}></path>

      {hLines.map((l) => (
        <line
          y1={l.y}
          y2={l.y}
          x1={0}
          x2={MAX_SIZE}
          strokeWidth={1}
          stroke="#000000"
        />
      ))}

      {vLines.map((l) => (
        <line
          y1={0}
          y2={MAX_SIZE}
          x1={l.x}
          x2={l.x}
          strokeWidth={1}
          stroke="#000000"
        />
      ))}
    </svg>
  );
};

export default App;
